import React, { Component } from "react";
import TutorialDataService from "../services/tutorial.service";
import { withRouter } from '../common/with-router';

class Tutorial extends Component {
  constructor(props) {
    super(props);
    this.onChangeTitle = this.onChangeTitle.bind(this);
    this.onChangeCampo = this.onChangeCampo.bind(this);
    this.getTutorial = this.getTutorial.bind(this);
    this.updatePublished = this.updatePublished.bind(this);
    this.updateTutorial = this.updateTutorial.bind(this);
    this.deleteTutorial = this.deleteTutorial.bind(this);

    this.state = {
      currentTutorial: {
        id: null,
        nome: "",
        email: "", 
        cpf: "",
        renda: null,
        telefone: "",
        data_criacao: "",
        title: "",
        description: "",
        //published: false
      },
      //message: ""
    };
  }

  componentDidMount() {
    this.getTutorial(this.props.router.params.id);
  }

  getTutorial(id) {
    TutorialDataService.get(id)
      .then(response => {
        this.setState({
          currentTutorial: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  onChangeTitle(e) {
    const nome = e.target.value;

    this.setState(function(prevState) {
      return {
        currentTutorial: {
          ...prevState.currentTutorial,
          nome: nome
        }
      };
    });
  }


  onChangeCampo(e) {
    const valor = e.target.value;

    this.setState(prevState => ({
      currentTutorial: {
        ...prevState.currentTutorial,
        email: valor
      }
    }));
  }


  updatePublished() {
    this.props.router.navigate('/tutorials');
    /*var data = {
      id: this.state.currentTutorial.id,
      title: this.state.currentTutorial.title,
      description: this.state.currentTutorial.description,
      published: status
    };

    TutorialDataService.update(this.state.currentTutorial.id, data)
      .then(response => {
        this.setState(prevState => ({
          currentTutorial: {
            ...prevState.currentTutorial,
            published: status
          }
        }));
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });*/
  }


  updateTutorial() {
    TutorialDataService.update(
      this.state.currentTutorial.id,
      this.state.currentTutorial
    )
      .then(response => {
        console.log(response.data);
        /*this.setState({
          message: "The tutorial was updated successfully!"
        });*/
      })
      .catch(e => {
        console.log(e);
      });
  }

  deleteTutorial() {
    TutorialDataService.delete(this.state.currentTutorial.id)
      .then(response => {
        console.log(response.data);
        this.props.router.navigate('/tutorials');
      })
      .catch(e => {
        console.log(e);
      });
  }

  render() {
    const { currentTutorial } = this.state;

    return (
      <div>
        {currentTutorial ? (
          <div className="edit-form">
            <h4>Cliente</h4>
            <form>


              <div className="form-group">
                <label htmlFor="title">Nome</label>
                <input
                  type="text"
                  className="form-control"
                  id="title"
                  value={currentTutorial.nome}
                  onChange={this.onChangeTitle}
                />
              </div>

              <div className="form-group">
                <label htmlFor="description">E-mail</label>
                <input
                  type="text"
                  className="form-control"
                  id="description"
                  value={currentTutorial.email}
                  onChange={this.onChangeDescription}
                />
              </div>

              <div className="form-group">
                <label htmlFor="description">CPF</label>
                <input
                  type="text"
                  className="form-control"
                  id="description"
                  value={currentTutorial.cpf}
                  //onChange={this.onChangeDescription}
                />
              </div>

              <div className="form-group">
                <label htmlFor="description">Renda</label>
                <input
                  type="text"
                  className="form-control"
                  id="description"
                  value={parseFloat(currentTutorial.renda).toLocaleString('pt-br',{style: 'currency', currency: 'BRL'})}
                  //onChange={this.onChangeDescription}
                />
              </div>

              <div className="form-group">
                <label htmlFor="description">Telefone</label>
                <input
                  type="text"
                  className="form-control"
                  id="description"
                  value={currentTutorial.telefone}
                  //onChange={this.onChangeDescription}
                />
              </div>

              <div className="form-group">
                <label htmlFor="description">Data de Criação</label>
                <input
                  type="DateTime-Local"
                  className="form-control"
                  id="description"
                  //value={"2023-09-27T22:59"}
                  //onChange={this.onChangeDescription}
                />
              </div>



            </form>

            <button
              className="badge badge-primary mr-2"
              onClick={() => this.updatePublished(true)}>
                Clientes
            </button>

            <button
              className="badge badge-danger mr-2"
              onClick={this.deleteTutorial}
            >
              Apaga
            </button>

            <button
              type="submit"
              className="badge badge-success"
              onClick={this.updateTutorial}
            >
              Atualiza
            </button>
            <p>{this.state.message}</p>
          </div>
        ) : (
          <div>
            <br />
            <p>Please click on a Tutorial...</p>
          </div>
        )}
      </div>
    );
  }
}

export default withRouter(Tutorial);
