  var usuario = valor.substring(0, valor.indexOf("@"));
  var dominio = valor.substring(valor.indexOf("@") + 1, valor.length);

  if((usuario.length >=1) &&
     (dominio.length >=3) &&
     (usuario.search("@")===-1) &&
     (dominio.search("@")===-1) &&
     (usuario.search(" ")===-1) &&
     (dominio.search(" ")===-1) &&
     (dominio.search(".")!==-1) &&
     (dominio.indexOf(".") >=1)&&
     (dominio.lastIndexOf(".") < dominio.length - 1)
  ) {
    this.setState({
      email: e.target.value
    });
    document.getElementById("labelmail").innerHTML="<font color='gren'>E-mail válido </font>";
    } else{
      document.getElementById("email").focus();
      document.getElementById("labelmail").innerHTML="<font color='red'>E-mail inválido </font>";
      }
}

