import React, { Component } from "react";
import ClienteDataService from "../services/cliente.service";
import { withRouter } from '../common/with-router';

class Cliente extends Component {
  constructor(props) {
    super(props);
    this.onChangeNome = this.onChangeNome.bind(this);
    this.onChangeEmail = this.onChangeEmail.bind(this);
    this.onChangeCPF = this.onChangeCPF.bind(this);
    this.onChangeRenda = this.onChangeRenda.bind(this);
    this.onChangeTelefone = this.onChangeTelefone.bind(this);
    this.getCliente = this.getCliente.bind(this);
    this.updatePublished = this.updatePublished.bind(this);
    this.updateCliente = this.updateCliente.bind(this);
    this.deleteTutorial = this.deleteTutorial.bind(this);

    this.state = {
      currentTutorial: {
        id: null,
        nome: "",
        email: "", 
        cpf: "",
        renda: null,
        telefone: "",
        data_criacao: "",
        title: "",
        description: "",
      },
    };
  }

  componentDidMount() {
    this.getCliente(this.props.router.params.id);
  }

  getCliente(id) {
    ClienteDataService.get(id)
      .then(response => {
        this.setState({
          currentTutorial: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  onChangeNome(e) {
    const nome = e.target.value;

    this.setState(function(prevState) {
      return {
        currentTutorial: {
          ...prevState.currentTutorial,
          nome: nome
        }
      };
    });
  }


  onChangeEmail(e) {
    const email = e.target.value;

    this.setState(prevState => ({
      currentTutorial: {
        ...prevState.currentTutorial,
        email: email
      }
    }));
  }

  onChangeCPF(e) {
    const cpf = e.target.value;

    this.setState(prevState => ({
      currentTutorial: {
        ...prevState.currentTutorial,
        cpf: cpf
      }
    }));
  }

  onChangeRenda(e) {
    const renda = e.target.value;

    this.setState(prevState => ({
      currentTutorial: {
        ...prevState.currentTutorial,
        renda: renda
      }
    }));
    parseFloat(renda).toLocaleString('pt-br',{style: 'currency', currency: 'BRL'})
  }

  onChangeTelefone(e) {
    const telefone = e.target.value;
    this.setState(prevState => ({
      currentTutorial: {
        ...prevState.currentTutorial,
        telefone: telefone
      }
    }));
  }

  updatePublished() {
    this.props.router.navigate('/clientes');
  }


  updateCliente() {
    ClienteDataService.update(
      this.state.currentTutorial.id,
      this.state.currentTutorial
    )
      .then(response => {
        console.log(response.data);
        this.props.router.navigate('/clientes');
        /*this.setState({
          message: "The tutorial was updated successfully!"
        });*/
      })
      .catch(e => {
        console.log(e);
      });
  }

  deleteTutorial() {
    ClienteDataService.delete(this.state.currentTutorial.id)
      .then(response => {
        console.log(response.data);
        this.props.router.navigate('/tutorials');
      })
      .catch(e => {
        console.log(e);
      });
  }

  render() {
    const { currentTutorial } = this.state;

    return (
      <div>
        {currentTutorial ? (
          <div className="edit-form">
            <h4>Cliente</h4>
            <form>


              <div className="form-group">
                <label htmlFor="title">Nome</label>
                <input
                  type="text"
                  className="form-control"
                  id="nome"
                  autoFocus
                  value={currentTutorial.nome}
                  onChange={this.onChangeNome}
                />
              </div>

              <div className="form-group">
                <label htmlFor="description">E-mail</label>
                <input
                  type="text"
                  className="form-control"
                  id="email"
                  value={currentTutorial.email}
                  onChange={this.onChangeEmail}
                />
              </div>

              <div className="form-group">
                <label htmlFor="description">CPF</label>
                <input
                  type="text"
                  className="form-control"
                  id="cpf"
                  value={currentTutorial.cpf}
                  onChange={this.onChangeCPF}
                />
              </div>

              <div className="form-group">
                <label htmlFor="description">Renda</label>
                <input
                  type="text"
                  className="form-control"
                  id="description"
                  onChange={this.onChangeRenda}
                />
              </div>

              <div className="form-group">
                <label htmlFor="description">Telefone</label>
                <input
                  type="text"
                  className="form-control"
                  id="description"
                  value={currentTutorial.telefone}
                  onChange={this.onChangeTelefone}
                />
              </div>

              <div className="form-group">
                <label htmlFor="description">Data de Criação</label>
                <input
                  type="DateTime-Local"
                  className="form-control"
                  id="description"
                />
              </div>



            </form>

            <button
              className="badge badge-primary mr-2"
              onClick={() => this.updatePublished(true)}>
                Clientes
            </button>

            <button
              className="badge badge-danger mr-2"
              onClick={this.deleteTutorial}
            >
              Apaga
            </button>

            <button
              type="submit"
              className="badge badge-success"
              onClick={this.updateCliente}
            >
              Atualiza
            </button>
            <p>{this.state.message}</p>
          </div>
        ) : (
          <div>
            <br />
            <p>Please click on a Tutorial...</p>
          </div>
        )}
      </div>
    );
  }
}

export default withRouter(Cliente);
