//import http from "../http-common";
import axios from 'axios';
import authHeader from './auth-header';

const API_URL = 'http://localhost:8090/api';

class ClienteDataService {
  getAll() {
    return axios.get(API_URL + "/clientes", { headers: { Authorization: 'Bearer' + JSON.parse(localStorage.getItem('user')) } });
  }

  get(id) {
    return axios.get(API_URL + `/clientes/${id}`, { headers: authHeader() });
  }

  create(data) {
    return axios.post(API_URL + "/clientes", data, { headers: authHeader() });
  }

  update(id, data) {
    return axios.put(API_URL + `/clientes/${id}`, { headers: authHeader() }, data);
  }

  delete(id) {
    return axios.delete(API_URL + `/clientes/${id}`, { headers: authHeader() });
  }

  deleteAll() {
    return axios.delete(API_URL + `/clientes`, { headers: authHeader() });
  }
}
// eslint-disable-next-line
export default new ClienteDataService();
